mapper.select:
select *
from ${prefix}ACT_HI_COMMENT
where TASK_ID_ = #{taskId,jdbcType=VARCHAR}
	and TYPE_ = #{type,jdbcType=VARCHAR}
order by TIME_ desc

select *
from ${prefix}ACT_HI_COMMENT
where PROC_INST_ID_ = #{processInstanceId,jdbcType=VARCHAR}
and TYPE_ = #{type,jdbcType=VARCHAR}
order by TIME_ desc
