mapper.select:
select * from ${prefix}ACT_EVT_LOG 
WHERE LOG_NR_ > #{parameter.startLogNr}
AND LOG_NR_ < #{parameter.endLogNr}
ORDER BY LOG_NR_

select * from ${prefix}ACT_EVT_LOG 
WHERE LOG_NR_ > #{parameter.startLogNr}
AND LOG_NR_ < #{parameter.endLogNr}
ORDER BY LOG_NR_

select * from ${prefix}ACT_EVT_LOG 
WHERE PROC_INST_ID_ = #{parameter.processInstanceId}
ORDER BY LOG_NR_

select * from ${prefix}ACT_EVT_LOG 
WHERE PROC_INST_ID_ = #{parameter.processInstanceId}
ORDER BY LOG_NR_
