mapper.update:
update ${prefix}ACT_ID_GROUP set
  REV_ = #{revisionNext ,jdbcType=INTEGER},
  NAME_ = #{name ,jdbcType=VARCHAR},
  TYPE_ = #{type ,jdbcType=VARCHAR}
where ID_ = #{id}
   and REV_ = #{revision}

mapper.select:
	${limitBefore}
  select g.*
  from ${prefix}ACT_ID_GROUP g, ${prefix}ACT_ID_MEMBERSHIP membership
  where g.ID_ = membership.GROUP_ID_
    and membership.USER_ID_ = #{parameter}
  ${limitAfter}

	${limitBefore}
  select g.*
  from ${prefix}ACT_ID_GROUP g, ${prefix}ACT_ID_MEMBERSHIP membership
  where g.ID_ = membership.GROUP_ID_
    and membership.USER_ID_ = #{parameter.userId}

       and g.TYPE_ = #{parameter.groupType}

  ${limitAfter}

mapper.sql:
from ${prefix}ACT_ID_GROUP G

  inner join ${prefix}ACT_ID_MEMBERSHIP M on G.ID_ = M.GROUP_ID_
  inner join ${prefix}ACT_ID_USER U on M.USER_ID_ = U.ID_



    G.ID_ = #{id}


    and G.NAME_ = #{name}


    and G.NAME_ like #{nameLike}


    and G.TYPE_ = #{type}


    and U.ID_ = #{userId}


    and exists (select ID_ from ${prefix} ACT_RU_IDENTITYLINK where PROC_DEF_ID_ = #{procDefId} and GROUP_ID_=G.ID_ )
