mapper.sql:
    from ${prefix}ACT_HI_PROCINST HPI
    
      inner join ACT_RE_PROCDEF DEF on HPI.PROC_DEF_ID_ = DEF.ID_
    
    
      inner join ${prefix}ACT_HI_PROCVARIABLE  A${index} on HPI.PROC_INST_ID_ = A${index}.PROC_INST_ID_
        
    
      
        HPI.PROC_INST_ID_ = #{processInstanceId}
      
      
        and HPI.PROC_INST_ID_ in
        
          #{item}
        
      
      
        and HPI.PROC_DEF_ID_ = #{processDefinitionId}
      
      
        and HPI.PROC_DEF_ID_ like #{processDefinitionIdLike}
      
      
        and HPI.BUSINESS_KEY_ = #{businessKey}
      

      
        and HPI.START_TIME_ <= #{startedBefore}
      
      
        and HPI.START_TIME_ >= #{startedAfter}
      
      
        and HPI.END_TIME_ <= #{finishedBefore}
      
      
        and HPI.END_TIME_ >= #{finishedAfter}
      
      
        
        and DEF.KEY_ not like #{procDefKey}
      
      
      
      
      
        and HPI.START_TIME_ >= #{startDateOnBegin}
        and HPI.START_TIME_ <= #{startDateOnEnd}
      
      
      
        and HPI.END_TIME_ >= #{finishDateOnBegin}
        and HPI.END_TIME_ <= #{finishDateOnEnd}
      
      
      
        and HPI.END_TIME_ <= #{finishDateBy}
      
      
      
        and HPI.START_TIME_ >= #{startDateBy}
      

      
        and HPI.END_TIME_ IS NULL
      
      
        and HPI.END_TIME_ is not NULL
      
      
        and HPI.START_USER_ID_ = #{startedBy}
      
      
        and HPI.SUPER_PROCESS_INSTANCE_ID_ = #{superProcessInstanceId}
      
      
        and A${index}.NAME_= #{queryVariableValue.name}
        
        
          and A${index}.VAR_TYPE_ = #{queryVariableValue.type}
        
        
        and A${index}.TEXT_
        
          LIKE
          
        
          #{queryVariableValue.textValue}
        
        
        and A${index}.TEXT2_
        
          LIKE
          
        
          #{queryVariableValue.textValue2}
        
        
        and A${index}.LONG_
        
        #{queryVariableValue.longValue}
        
        
        and A${index}.DOUBLE_
        
        #{queryVariableValue.doubleValue}
        
        
        
          
          
            and (A${index}.TEXT_ is not null or A${index}.TEXT2_ is not null or A${index}.LONG_ is not null or A${index}.DOUBLE_ is not null or A${index}.BYTEARRAY_ID_ is not null)
          
          
            and A${index}.TEXT_ is null and A${index}.TEXT2_ is null and A${index}.LONG_ is null and A${index}.DOUBLE_ is null and A${index}.BYTEARRAY_ID_ is null
