mapper.select:
select * from ACT_RU_JOB
where HANDLER_TYPE_ = #{handlerType}
and HANDLER_CFG_ =  #{handlerConfiguration}

select *
from ACT_RU_JOB J
where J.EXECUTION_ID_ = #{executionId}

mapper.sql:
from ACT_RU_JOB J


    J.ID_ = #{id}


    and J.PROCESS_INSTANCE_ID_ = #{processInstanceId}


    and J.EXECUTION_ID_ = #{executionId}


    and J.RETRIES_ > 0


    (J.RETRIES_ > 0)
    and (J.DUEDATE_ is null or J.DUEDATE_ <= #{now, jdbcType=TIMESTAMP})


    and J.TYPE_ = 'timer'


    and J.TYPE_ = 'message'


    and J.DUEDATE_ > #{duedateHigherThen}


    and J.DUEDATE_ < #{duedateLowerThen}


    and J.DUEDATE_ >= #{duedateHigherThenOrEqual}


    and J.DUEDATE_ <= #{duedateLowerThenOrEqual}


  	and J.EXCEPTION_MSG_ is not null


  	and J.EXCEPTION_MSG_ = #{exceptionMessage}

mapper.update:
update ACT_RU_JOB

   REV_ =  #{revisionNext, jdbcType=INTEGER},
   LOCK_EXP_TIME_ = #{lockExpirationTime, jdbcType=TIMESTAMP},
   LOCK_OWNER_ = #{lockOwner, jdbcType=VARCHAR},
   RETRIES_ = #{retries, jdbcType=INTEGER},
   EXCEPTION_STACK_ID_ = #{exceptionByteArrayId, jdbcType=VARCHAR},
   EXCEPTION_MSG_ = #{exceptionMessage, jdbcType=VARCHAR},
   DUEDATE_ = #{duedate, jdbcType=TIMESTAMP}

where ID_= #{id, jdbcType=VARCHAR}
  and REV_ = #{revision, jdbcType=INTEGER}

update ACT_RU_JOB

   REV_ =  #{revisionNext, jdbcType=INTEGER},
   LOCK_EXP_TIME_ = #{lockExpirationTime, jdbcType=TIMESTAMP},
   LOCK_OWNER_ = #{lockOwner, jdbcType=VARCHAR},
   RETRIES_ = #{retries, jdbcType=INTEGER},
   EXCEPTION_STACK_ID_ = #{exceptionByteArrayId, jdbcType=VARCHAR},
   EXCEPTION_MSG_ = #{exceptionMessage, jdbcType=VARCHAR}

where ID_= #{id, jdbcType=VARCHAR}
  and REV_ = #{revision, jdbcType=INTEGER}
