mapper.update:
update ${prefix}ACT_RU_TASK
REV_ = #{revisionNext, jdbcType=INTEGER}, NAME_ = #{name, jdbcType=VARCHAR}, PARENT_TASK_ID_ = #{parentTaskId, jdbcType=VARCHAR}, PRIORITY_ = #{priority, jdbcType=INTEGER}, CREATE_TIME_ = #{createTime, jdbcType=TIMESTAMP}, OWNER_ = #{owner, jdbcType=VARCHAR}, ASSIGNEE_ = #{assignee, jdbcType=VARCHAR}, DELEGATION_ = #{delegationStateString, jdbcType=VARCHAR}, EXECUTION_ID_ = #{executionId, jdbcType=VARCHAR}, PROC_DEF_ID_ = #{processDefinitionId, jdbcType=VARCHAR}, DESCRIPTION_ = #{description, jdbcType=VARCHAR}, DUE_DATE_ = #{dueDate, jdbcType=TIMESTAMP}, CATEGORY_ = #{category, jdbcType=VARCHAR}, SUSPENSION_STATE_ = #{suspensionState, jdbcType=INTEGER}, FORM_KEY_ = #{formKey, jdbcType=INTEGER}
where ID_= #{id, jdbcType=VARCHAR} and REV_ = #{revision, jdbcType=INTEGER}

update ${prefix}ACT_RU_TASK set
  TENANT_ID_ = #{tenantId, jdbcType=VARCHAR}
where
  ID_ in (
    SELECT T.ID_ from ${prefix}ACT_RU_TASK T
    inner join ${prefix}ACT_RE_PROCDEF P on T.PROC_DEF_ID_  = P.ID_
    inner join ${prefix}ACT_RE_DEPLOYMENT D on P.DEPLOYMENT_ID_ = D.ID_ 
    where D.ID_ = #{deploymentId, jdbcType=VARCHAR}
  )

update ${prefix}ACT_RU_TASK set
  TENANT_ID_ = #{tenantId, jdbcType=VARCHAR}
where
  ID_ in (

      SELECT tempTask.tempId
      FROM (
            SELECT T.ID_ as tempId
            FROM  ${prefix}ACT_RU_TASK T
            inner join ${prefix}ACT_RE_PROCDEF P on T.PROC_DEF_ID_  = P.ID_
            inner join ${prefix}ACT_RE_DEPLOYMENT D on P.DEPLOYMENT_ID_ = D.ID_ 
            where D.ID_ = #{deploymentId, jdbcType=VARCHAR}

    ) AS tempTask 

)

mapper.select:
select distinct T.*
from ${prefix}ACT_RU_TASK T
where T.EXECUTION_ID_ = #{parameter}

select T.*
from ${prefix}ACT_RU_TASK T
where T.PROC_INST_ID_ = #{parameter}

${limitBefore}
select RES.*,
VAR.ID_ as VAR_ID_, VAR.NAME_ as VAR_NAME_, VAR.TYPE_ as VAR_TYPE_, VAR.REV_ as VAR_REV_,
VAR.PROC_INST_ID_ as VAR_PROC_INST_ID_, VAR.EXECUTION_ID_ as VAR_EXECUTION_ID_, VAR.TASK_ID_ as VAR_TASK_ID_,
VAR.BYTEARRAY_ID_ as VAR_BYTEARRAY_ID_, VAR.DOUBLE_ as VAR_DOUBLE_, 
VAR.TEXT_ as VAR_TEXT_, VAR.TEXT2_ as VAR_TEXT2_, VAR.LONG_ as VAR_LONG_
${limitBetween}

${orderBy}
${limitAfter}

where.if:
and RES.ASSIGNEE_ is null and I.TYPE_ = 'candidate' and (
I.USER_ID_ = #{candidateUser}
or
I.GROUP_ID_ IN
#{group}
)

and (
  exists(select LINK.USER_ID_ from ${prefix}ACT_RU_IDENTITYLINK LINK where USER_ID_ = #{involvedUser} and LINK.TASK_ID_ = RES.ID_)
  or RES.ASSIGNEE_ = #{involvedUser}
  or RES.OWNER_ = #{involvedUser}
  )

when.if:
and (RES.ASSIGNEE_ = #{userIdForCandidateAndAssignee}
or (RES.ASSIGNEE_ is null
and I.TYPE_ = 'candidate' and (I.USER_ID_ = #{userIdForCandidateAndAssignee}

  or I.GROUP_ID_ IN

    #{group}


)))

where.if:
and 


      RES.ID_ = #{orQueryObject.taskId}


      or RES.NAME_ = #{orQueryObject.name}


      or RES.NAME_ like #{orQueryObject.nameLike}


      or lower(RES.NAME_) like #{orQueryObject.nameLikeIgnoreCase}


      or RES.NAME_ IN

        #{name}



      or lower(RES.NAME_) IN

        #{name}



      or RES.DESCRIPTION_ = #{orQueryObject.description}


      or RES.DESCRIPTION_ like #{orQueryObject.descriptionLike}


      or lower(RES.DESCRIPTION_) like #{orQueryObject.descriptionLikeIgnoreCase}


      or RES.PRIORITY_ = #{orQueryObject.priority}


      or RES.PRIORITY_ >= #{orQueryObject.minPriority}


      or RES.PRIORITY_ <= #{orQueryObject.maxPriority}


      or RES.ASSIGNEE_ = #{orQueryObject.assignee}


      or RES.ASSIGNEE_ like #{orQueryObject.assigneeLike}


      or lower(RES.ASSIGNEE_) like #{orQueryObject.assigneeLikeIgnoreCase}


      or RES.OWNER_ = #{orQueryObject.owner}


      or RES.OWNER_ like #{orQueryObject.ownerLike}


      or RES.OWNER_ like #{orQueryObject.ownerLikeIgnoreCase}


      or RES.ASSIGNEE_ IS NULL


      or RES.DELEGATION_ IS NULL


      or RES.DELEGATION_ = #{orQueryObject.delegationStateString}


      or RES.PROC_INST_ID_ = #{orQueryObject.processInstanceId}


      or E.BUSINESS_KEY_ = #{orQueryObject.processInstanceBusinessKey}


      or E.BUSINESS_KEY_ like #{orQueryObject.processInstanceBusinessKeyLike}


      or lower(E.BUSINESS_KEY_) like #{orQueryObject.processInstanceBusinessKeyLikeIgnoreCase}


      or RES.EXECUTION_ID_ = #{orQueryObject.executionId}


      or RES.CREATE_TIME_ = #{orQueryObject.createTime}


      or RES.CREATE_TIME_ < #{orQueryObject.createTimeBefore}


      or RES.CREATE_TIME_ > #{orQueryObject.createTimeAfter}


      or RES.TASK_DEF_KEY_ = #{orQueryObject.key}


      or RES.TASK_DEF_KEY_ like #{orQueryObject.keyLike}


      or RES.PROC_DEF_ID_ = #{orQueryObject.processDefinitionId}


      or D.KEY_ = #{orQueryObject.processDefinitionKey}


      or D.KEY_ like #{orQueryObject.processDefinitionKeyLike}


      or lower(D.KEY_) like #{orQueryObject.processDefinitionKeyLikeIgnoreCase}


      or D.NAME_ = #{orQueryObject.processDefinitionName}


      or D.NAME_ like #{orQueryObject.processDefinitionNameLike}


      or D.CATEGORY_ IN

        #{processCategory}



      or D.CATEGORY_ NOT IN

        #{processCategory}



      or DEPLOY_P.DEPLOYMENT_ID_ = #{orQueryObject.deploymentId}


      or DEPLOY_P.DEPLOYMENT_ID_ IN

        #{deployment}



      or RES.DUE_DATE_ = #{orQueryObject.dueDate}


      or (RES.DUE_DATE_ < #{orQueryObject.dueBefore} and RES.DUE_DATE_ is not null)


      or (RES.DUE_DATE_ > #{orQueryObject.dueAfter} and RES.DUE_DATE_ is not null)


      or RES.DUE_DATE_ is null


      or RES.CATEGORY_ = #{orQueryObject.category}


      or RES.PARENT_TASK_ID_ IS NULL



      or (RES.ASSIGNEE_ is null
      and I.TYPE_ = 'candidate'
      and 
      ( 

          I.USER_ID_ = #{orQueryObject.candidateUser}          


          or


          I.GROUP_ID_ IN

            #{group}


      ))


      or (
        exists(select LINK.USER_ID_ from ${prefix}ACT_RU_IDENTITYLINK LINK where USER_ID_ = #{orQueryObject.involvedUser} and LINK.TASK_ID_ = RES.ID_)
        or RES.ASSIGNEE_ = #{orQueryObject.involvedUser}
        or RES.OWNER_ = #{orQueryObject.involvedUser}
        )


      or 



        and A_OR${index}.TASK_ID_ is null



        and A_OR${index}.NAME_= #{var.name}


  		and A_OR${index}.NAME_ is not null


        and A_OR${index}.TYPE_ = #{var.type}





            and lower(A_OR${index}.TEXT_)


            and A_OR${index}.TEXT_



            LIKE


        #{var.textValue}


        and A_OR${index}.TEXT2_ 

          LIKE


        #{var.textValue2}


        and A_OR${index}.LONG_

        #{var.longValue}


        and A_OR${index}.DOUBLE_ 

        #{var.doubleValue}





            and (A_OR${index}.TEXT_ is not null or A_OR${index}.TEXT2_ is not null or A_OR${index}.LONG_ is not null or A_OR${index}.DOUBLE_ is not null or A_OR${index}.BYTEARRAY_ID_ is not null)


            and A_OR${index}.TEXT_ is null and A_OR${index}.TEXT2_ is null and A_OR${index}.LONG_ is null and A_OR${index}.DOUBLE_ is null and A_OR${index}.BYTEARRAY_ID_ is null







          or RES.SUSPENSION_STATE_ = 1


          or RES.SUSPENSION_STATE_ = 2



      or RES.TENANT_ID_ = #{orQueryObject.tenantId}


      or RES.TENANT_ID_ like #{orQueryObject.tenantIdLike}


      or (RES.TENANT_ID_ = '' or RES.TENANT_ID_ is null)






            or (RES.ASSIGNEE_ = #{orQueryObject.userIdForCandidateAndAssignee} or (RES.ASSIGNEE_ is null and (I.USER_ID_ = #{orQueryObject.userIdForCandidateAndAssignee}
            or I.GROUP_ID_ IN (select g.GROUP_ID_ from ${prefix}ACT_ID_MEMBERSHIP g where g.USER_ID_ = #{orQueryObject.userIdForCandidateAndAssignee} ) ) ) )




          or (RES.ASSIGNEE_ = #{orQueryObject.userIdForCandidateAndAssignee}
          or (RES.ASSIGNEE_ is null
          and I.TYPE_ = 'candidate' and (I.USER_ID_ = #{orQueryObject.userIdForCandidateAndAssignee}

            or I.GROUP_ID_ IN

              #{group}


          )))
