mapper.sql:
from ${prefix}ACT_RU_EVENT_SUBSCR RES


    RES.ID_ = #{eventSubscriptionId}


    and RES.PROC_INST_ID_ = #{processInstanceId}


    and RES.EXECUTION_ID_ = #{executionId}


    and RES.ACTIVITY_ID_ = #{activityId}


    and RES.EVENT_TYPE_ = #{eventType}


    and RES.EVENT_NAME_ = #{eventName}


    and RES.TENANT_ID_ = #{parameter.tenantId}


    and (RES.TENANT_ID_ = '' or RES.TENANT_ID_ is null)

mapper.select:
select * from ${prefix}ACT_RU_EVENT_SUBSCR EVT left outer join ${prefix}ACT_RU_EXECUTION EXC on EVT.EXECUTION_ID_ = EXC.ID_ where EVENT_TYPE_ = 'signal' and EVENT_NAME_ = #{parameter.eventName} and (EVT.EXECUTION_ID_ is null OR (EVT.EXECUTION_ID_ is not null AND EXC.SUSPENSION_STATE_ = 1) )
and EVT.TENANT_ID_ = #{parameter.tenantId}
and (EVT.TENANT_ID_ = '' or EVT.TENANT_ID_ is null)

select * 
from ${prefix}ACT_RU_EVENT_SUBSCR EVT
where (EVENT_TYPE_ = 'signal')
    and EVENT_NAME_ = #{parameter.eventName}  
    and PROC_INST_ID_ = #{parameter.processInstanceId}

select * from ${prefix}ACT_RU_EVENT_SUBSCR where (EVENT_TYPE_ = #{parameter.eventType}) and ( (CONFIGURATION_ = #{parameter.configuration}) or (PROC_DEF_ID_ = #{parameter.configuration}) )
and TENANT_ID_ = #{parameter.tenantId}
and (TENANT_ID_ = '' or TENANT_ID_ is null)

select * from ${prefix}ACT_RU_EVENT_SUBSCR where (EVENT_TYPE_ = #{parameter.eventType}) and (EVENT_NAME_ = #{parameter.eventName})
and TENANT_ID_ = #{parameter.tenantId}
and (TENANT_ID_ = '' or TENANT_ID_ is null)

select * from ${prefix}ACT_RU_EVENT_SUBSCR where (EVENT_TYPE_ = 'message') and (EVENT_NAME_ = #{eventName}) and EXECUTION_ID_ is null
and TENANT_ID_ = #{tenantId}
and (TENANT_ID_ = '' or TENANT_ID_ is null)

mapper.update:
update ${prefix}ACT_RU_EVENT_SUBSCR
REV_ = #{revisionNext, jdbcType=INTEGER}, EVENT_NAME_ = #{eventName, jdbcType=INTEGER}, EXECUTION_ID_ = #{executionId, jdbcType=INTEGER}, PROC_INST_ID_ = #{processInstanceId, jdbcType=INTEGER}, ACTIVITY_ID_ = #{activityId, jdbcType=INTEGER}, CONFIGURATION_ = #{configuration, jdbcType=VARCHAR}, CREATED_ = #{created, jdbcType=TIMESTAMP}, PROC_DEF_ID_ = #{processDefinitionId, jdbcType=VARCHAR}, TENANT_ID_ = #{tenantId, jdbcType=VARCHAR}
where ID_= #{id, jdbcType=VARCHAR} and REV_ = #{revision, jdbcType=INTEGER}

update ${prefix}ACT_RU_EVENT_SUBSCR
REV_ = #{revisionNext, jdbcType=INTEGER}, EVENT_NAME_ = #{eventName, jdbcType=INTEGER}, EXECUTION_ID_ = #{executionId, jdbcType=INTEGER}, PROC_INST_ID_ = #{processInstanceId, jdbcType=INTEGER}, ACTIVITY_ID_ = #{activityId, jdbcType=INTEGER}, CONFIGURATION_ = #{configuration, jdbcType=VARCHAR}, CREATED_ = #{created, jdbcType=TIMESTAMP}, TENANT_ID_ = #{tenantId, jdbcType=VARCHAR}
where ID_= #{id, jdbcType=VARCHAR} and REV_ = #{revision, jdbcType=INTEGER}

update ${prefix}ACT_RU_EVENT_SUBSCR
REV_ = #{revisionNext, jdbcType=INTEGER}, EVENT_NAME_ = #{eventName, jdbcType=INTEGER}, EXECUTION_ID_ = #{executionId, jdbcType=INTEGER}, PROC_INST_ID_ = #{processInstanceId, jdbcType=INTEGER}, ACTIVITY_ID_ = #{activityId, jdbcType=INTEGER}, CONFIGURATION_ = #{configuration, jdbcType=VARCHAR}, CREATED_ = #{created, jdbcType=TIMESTAMP}, TENANT_ID_ = #{tenantId, jdbcType=VARCHAR}
where ID_= #{id, jdbcType=VARCHAR} and REV_ = #{revision, jdbcType=INTEGER}

update ${prefix}ACT_RU_EVENT_SUBSCR

  TENANT_ID_ = #{newTenantId, jdbcType=VARCHAR}

where TENANT_ID_ = #{oldTenantId, jdbcType=VARCHAR}
